changeSet.sql:
drop table if exists TMP_ASSIGNMENT;
create table if not exists TMP_ASSIGNMENT as ( select T_ASSIGNMENT.assignment_id, T_ASSIGNMENT.assign_person_id, T_ASSIGNMENT.respond_person_id, T_ASSIGNMENT.rejection_note_id, REJECTION_NOTE_PERSON.person_id as rejection_note_person_id from R_EAVROP_ASSIGNMENT inner join T_ASSIGNMENT on R_EAVROP_ASSIGNMENT.assignment_id=T_ASSIGNMENT.assignment_id left outer join T_NOTE as REJECTION_NOTE on T_ASSIGNMENT.rejection_note_id=REJECTION_NOTE.note_id left outer join T_PERSON as REJECTION_NOTE_PERSON on REJECTION_NOTE.person_id=REJECTION_NOTE_PERSON.person_id where R_EAVROP_ASSIGNMENT.eavrop_id in(${eavrops}) );
delete from R_EAVROP_ASSIGNMENT where R_EAVROP_ASSIGNMENT.assignment_id in( select assignment_id from TMP_ASSIGNMENT);
delete from T_ASSIGNMENT where T_ASSIGNMENT.assignment_id in( select assignment_id from TMP_ASSIGNMENT);
delete from T_NOTE where T_NOTE.note_id in(select rejection_note_id from TMP_ASSIGNMENT);
delete from T_PERSON where T_PERSON.person_id in(select assign_person_id from TMP_ASSIGNMENT) or T_PERSON.person_id in(select respond_person_id from TMP_ASSIGNMENT) or T_PERSON.person_id in(select rejection_note_person_id from TMP_ASSIGNMENT);
drop table if exists TMP_ASSIGNMENT;


drop  table if exists TMP_NOTE;

create  table if not exists TMP_NOTE as (
    select T_NOTE.note_id, T_NOTE.person_id
    from R_EAVROP_NOTE
    inner join T_NOTE on R_EAVROP_NOTE.note_id=T_NOTE.note_id
    where R_EAVROP_NOTE.eavrop_id in(${eavrops})
);

delete  
from R_EAVROP_NOTE
where R_EAVROP_NOTE.note_id in(select note_id from TMP_NOTE);

delete  
from T_NOTE
where T_NOTE.note_id in(select note_id from TMP_NOTE);

delete  
from T_PERSON
where T_PERSON.person_id in(select person_id from TMP_NOTE);

drop  table if exists TMP_NOTE;


        drop  table if exists TMP_INTYG;

        create  table if not exists TMP_INTYG as (
	        select T_INTYG_INFORMATION.intyg_information_id, T_INTYG_INFORMATION.person_id 
	        from R_EAVROP_INTYG
	        inner join T_INTYG_INFORMATION on R_EAVROP_INTYG.intyg_information_id=T_INTYG_INFORMATION.intyg_information_id
	        where R_EAVROP_INTYG.eavrop_id in(${eavrops})
        );

        delete  
        from R_EAVROP_INTYG
        where R_EAVROP_INTYG.intyg_information_id in(select intyg_information_id from TMP_INTYG);

        delete  
        from T_INTYG_INFORMATION
        where T_INTYG_INFORMATION.intyg_information_id in(select intyg_information_id from TMP_INTYG);

        delete  
        from T_PERSON
        where T_PERSON.person_id in(select person_id from TMP_INTYG);

        drop  table if exists TMP_INTYG;


        drop  table if exists TMP_REC_DOCUMENT;

        create  table if not exists TMP_REC_DOCUMENT as (
	        select T_REC_DOCUMENT.document_id, T_REC_DOCUMENT.person_id 
