	        inner join T_REC_DOCUMENT on R_EAVROP_REC_DOCUMENT.document_id=T_REC_DOCUMENT.document_id
	        where R_EAVROP_REC_DOCUMENT.eavrop_id in(${eavrops})    
        );

        delete  
        from R_EAVROP_REC_DOCUMENT
        where R_EAVROP_REC_DOCUMENT.document_id in(select document_id from TMP_REC_DOCUMENT);

        delete  
        from T_REC_DOCUMENT
        where T_REC_DOCUMENT.document_id in(select document_id from TMP_REC_DOCUMENT);

