        from T_PERSON
        where T_PERSON.person_id in(select person_id from TMP_REC_DOCUMENT);

        drop  table if exists TMP_REC_DOCUMENT;

drop  table if exists TMP_NOTE;

create  table if not exists TMP_NOTE as (
    select T_NOTE.note_id,T_NOTE.person_id  
    from R_EAVROP_NOTE
    inner join T_NOTE on R_EAVROP_NOTE.note_id=T_NOTE.note_id
    where R_EAVROP_NOTE.eavrop_id in(${eavrops})
);

delete  
from R_EAVROP_NOTE
where R_EAVROP_NOTE.note_id in( select note_id from TMP_NOTE);

delete  
from T_NOTE
where T_NOTE.note_id in( select note_id from TMP_NOTE);

delete  
from T_PERSON
where T_PERSON.person_id in(select person_id from TMP_NOTE);

drop  table if exists TMP_NOTE;

drop  table if exists TMP_REQ_DOCUMENT;

create  table if not exists TMP_REQ_DOCUMENT as (
    select T_REQ_DOCUMENT.document_id, T_REQ_DOCUMENT.person_id as req_person_id, T_NOTE.note_id, T_NOTE.person_id as req_note_person_id 
    from R_EAVROP_REQ_DOCUMENT 
    inner join T_REQ_DOCUMENT on R_EAVROP_REQ_DOCUMENT.document_id = T_REQ_DOCUMENT.document_id
    left outer join T_NOTE on T_REQ_DOCUMENT.request_note_id = T_NOTE.note_id
    where R_EAVROP_REQ_DOCUMENT.eavrop_id in(${eavrops})
);

delete  
from R_EAVROP_REQ_DOCUMENT
where R_EAVROP_REQ_DOCUMENT.document_id in( select document_id from TMP_REQ_DOCUMENT);

delete  
from T_REQ_DOCUMENT
where T_REQ_DOCUMENT.document_id in( select document_id from TMP_REQ_DOCUMENT);

delete  
from T_NOTE
where T_NOTE.note_id in( select note_id from TMP_REQ_DOCUMENT);

delete  
from T_PERSON
where T_PERSON.person_id in(select req_person_id from TMP_REQ_DOCUMENT)
or T_PERSON.person_id in(select req_note_person_id from TMP_REQ_DOCUMENT);

drop  table if exists TMP_REQ_DOCUMENT;

drop table if exists TMP_BOOKING;
create table if not exists TMP_BOOKING as ( select T_BOOKING.booking_id, T_BOOKING.deviation_response_person_id, T_BOOKING.deviation_response_note_id, DEV_RESP_NOTE.person_id as dev_resp_note_person_id, T_BOOKING.interpreter_deviation_note_id, INT_DEV_NOTE.person_id as int_dev_note_person_id, T_BOOKING.deviation_note_id, DEV_NOTE.person_id as dev_note_person_id from R_EAVROP_BOOKING inner join T_BOOKING on R_EAVROP_BOOKING.booking_id=T_BOOKING.booking_id left outer join T_NOTE as DEV_RESP_NOTE on T_BOOKING.deviation_response_note_id=DEV_RESP_NOTE.note_id left outer join T_NOTE as INT_DEV_NOTE on T_BOOKING.interpreter_deviation_note_id=INT_DEV_NOTE.note_id left outer join T_NOTE as DEV_NOTE on T_BOOKING.deviation_note_id=DEV_NOTE.note_id where R_EAVROP_BOOKING.eavrop_id in(${eavrops}) );
delete from R_EAVROP_BOOKING where R_EAVROP_BOOKING.booking_id in( select booking_id from TMP_BOOKING);
delete from T_BOOKING where T_BOOKING.booking_id in( select booking_id from TMP_BOOKING);
delete from T_NOTE where T_NOTE.note_id in(select deviation_response_note_id from TMP_BOOKING) or T_NOTE.note_id in(select interpreter_deviation_note_id from TMP_BOOKING) or T_NOTE.note_id in(select deviation_note_id from TMP_BOOKING);
delete from T_PERSON where T_PERSON.person_id in(select deviation_response_person_id from TMP_BOOKING) or T_PERSON.person_id in(select dev_resp_note_person_id from TMP_BOOKING) or T_PERSON.person_id in(select int_dev_note_person_id from TMP_BOOKING) or T_PERSON.person_id in(select dev_note_person_id from TMP_BOOKING);
drop table if exists TMP_BOOKING;

drop table if exists TMP_EAVROP;
create table if not exists TMP_EAVROP as ( select T_EAVROP.eavrop_id, T_EAVROP.bestallar_person_id, T_EAVROP.invanare_id, T_EAVROP.prior_examination_id, T_PRIOR_MEDICAL_EXAMINATION.person_id as prior_med_person_id, T_EAVROP.eavrop_approval_id, T_EAVROP_APPROVAL.person_id as approval_person_id, T_EAVROP_APPROVAL.note_id as approval_note_id, APPROVAL_NOTE.person_id as approval_note_person_id, T_EAVROP.eavrop_comp_approval_id, T_EAVROP_COMP_APPROVAL.person_id as comp_approval_person_id, T_EAVROP_COMP_APPROVAL.note_id as comp_approval_note_id, COMP_APPROVAL_NOTE.person_id as comp_approval_note_person_id from T_EAVROP left outer join T_PRIOR_MEDICAL_EXAMINATION on T_EAVROP.prior_examination_id = T_PRIOR_MEDICAL_EXAMINATION.prior_medical_exam_id left outer join T_EAVROP_APPROVAL on T_EAVROP.eavrop_approval_id = T_EAVROP_APPROVAL.eavrop_approval_id left outer join T_NOTE as APPROVAL_NOTE on T_EAVROP_APPROVAL.note_id = APPROVAL_NOTE.note_id left outer join T_EAVROP_COMP_APPROVAL on T_EAVROP.eavrop_comp_approval_id = T_EAVROP_COMP_APPROVAL.id left outer join T_NOTE as COMP_APPROVAL_NOTE on T_EAVROP_COMP_APPROVAL.note_id = COMP_APPROVAL_NOTE.note_id where T_EAVROP.eavrop_id in(${eavrops}) );
delete from T_EAVROP where T_EAVROP.eavrop_id in(select eavrop_id from TMP_EAVROP);
delete from T_INVANARE where T_INVANARE.invanare_id in(select invanare_id from TMP_EAVROP);
delete from T_PRIOR_MEDICAL_EXAMINATION where T_PRIOR_MEDICAL_EXAMINATION.prior_medical_exam_id in(select prior_medical_exam_id from TMP_EAVROP);
delete from T_EAVROP_APPROVAL where T_EAVROP_APPROVAL.eavrop_approval_id in(select eavrop_approval_id from TMP_EAVROP);
delete from T_EAVROP_COMP_APPROVAL where T_EAVROP_COMP_APPROVAL.id in(select eavrop_comp_approval_id from TMP_EAVROP);
delete from T_NOTE where T_NOTE.note_id in(select approval_note_id from TMP_EAVROP) or T_NOTE.note_id in(select comp_approval_note_id from TMP_EAVROP);
delete from T_PERSON where T_PERSON.person_id in(select bestallar_person_id from TMP_EAVROP) or T_PERSON.person_id in(select prior_med_person_id from TMP_EAVROP) or T_PERSON.person_id in(select approval_person_id from TMP_EAVROP) or T_PERSON.person_id in(select approval_note_person_id from TMP_EAVROP) or T_PERSON.person_id in(select comp_approval_person_id from TMP_EAVROP) or T_PERSON.person_id in(select comp_approval_note_person_id from TMP_EAVROP);
drop table if exists TMP_EAVROP;
